
public class Ejercicio8Java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int num1 = (int) Math.pow(2, 8);
	System.out.println("El rango de byte es : "+ num1/2);
	int num2 = (int) Math.pow(2, 16);
	System.out.println("El rango de short es : "+ num2/2);
	long num3 = (long) Math.pow(2, 32);
	System.out.println("El rango de int es : "+ num3/2);
	long num4 = (long) Math.pow(2, 64);
	System.out.println("El rango de long es : "+ num4/2);
	}

}
